mapper.select:
SELECT 
FROM y_credit_products

     AND status = #{status}
     AND product_type = #{productType}
     AND bank_name LIKE CONCAT('%', #{bankName}, '%')

ORDER BY id DESC

    LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM y_credit_products

     AND status = #{status}
     AND product_type = #{productType}
     AND bank_name LIKE CONCAT('%', #{bankName}, '%')

SELECT 
FROM y_credit_products

    AND status = 1
     AND min_amount <= #{expectedAmount} AND max_amount >= #{expectedAmount}
     AND loan_term >= #{expectedTerm}
     AND region LIKE CONCAT('%', #{regionCode}, '%')
     AND product_type = #{productType}

ORDER BY success_rate DESC, id DESC

    LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM y_credit_products

    AND status = 1
     AND min_amount <= #{expectedAmount} AND max_amount >= #{expectedAmount}
     AND loan_term >= #{expectedTerm}
     AND region LIKE CONCAT('%', #{regionCode}, '%')
     AND product_type = #{productType}

mapper.update:
UPDATE y_credit_products
bank_name = #{bankName}, product_name = #{productName}, product_type = #{productType}, min_amount = #{minAmount}, max_amount = #{maxAmount}, interest_rate_range = #{interestRateRange}, loan_term = #{loanTerm}, repayment_method = #{repaymentMethod}, region = #{region}, criteria_json = #{criteriaJson}, status = #{status}, success_rate = #{successRate},
WHERE id = #{id}
